$(document).ready(function(){
	
	$.getJSON("/api/todos")
	.then(addTodos);
	
	$.post("/api/todos", {name: $("#todoInput").val()})
	.then()
	
	function addTodos(todos){
		todos.forEach(function(todo){
			var newTodo = $('<li class="task">'+ todo.name +'</li>');
			if(todo.completed){
				newTodo.addClass("done");
			}
			$(".list").append(newTodo);
		});
	}
});